export declare const speechBubbleStyles = "\n/* @keyframes SpeechBubble-visibleLetterAnimation {\n  0% {\n    opacity: 0.75;\n  }\n  50% {\n    opacity: 1;\n  }\n  100% {\n    opacity: 0.75;\n  }\n} */\n\n.SpeechBubble-hiddenLetter {\n  opacity: 0;\n  transform: scale(0.5);\n  transition: opacity 0.2s ease-in-out, transform 0.2s ease-in-out;\n  will-change: opacity, transform;\n  margin-top: -8px;\n  margin-bottom: -8px;\n}\n.SpeechBubble-visibleLetter {\n  opacity: 1;\n  transform: scale(1);\n  transition: opacity 0.2s ease-in-out, transform 0.2s ease-in-out;\n  will-change: opacity, transform;\n  margin-top: -8px;\n  margin-bottom: -8px;\n  /* animation: SpeechBubble-visibleLetterAnimation 1s 0s infinite; */\n}\n\n.SpeechBubble-wordLettersHolder {\n  display: flex;\n  align-items: center;\n  justify-content: flex-start;\n  flex-direction: row;\n  flex-wrap: no-wrap;\n  white-space: pre;\n}\n\n";
